<template>
  <div class="vue-simplemde">
    <textarea
      class="vue-simplemde-textarea"
      :name="name"
      :value="modelValue"
      @input="handleInput($event.target.value)"
    />
  </div>
</template>

<script>
import SimpleMDE from 'simplemde';
import marked from 'marked';

export default {
  name: 'vue-simplemde',
  model: {
    prop: 'modelValue',
    event: 'update:modelValue',
  },
  props: {
    value: String,
    modelValue: String,
    name: String,
    previewClass: String,
    autoinit: {
      type: Boolean,
      default() {
        return true;
      },
    },
    forceSync: {
      type: Boolean,
      default() {
        return true;
      },
    },
    highlight: {
      type: Boolean,
      default() {
        return false;
      },
    },
    sanitize: {
      type: Boolean,
      default() {
        return false;
      },
    },
    configs: {
      type: Object,
      default() {
        return {};
      },
    },
    previewRender: {
      type: Function,
    },
  },
  data() {
    return {
      isValueUpdateFromInner: false,
    };
  },
  mounted() {
    if (this.autoinit) this.initialize();
  },
  deactivated() {
    const editor = this.simplemde;
    if (!editor) return;
    const isFullScreen = editor.codemirror.getOption('fullScreen');
    if (isFullScreen) editor.toggleFullScreen();
  },
  methods: {
    initialize() {
      const configs = Object.assign({
        element: this.$el.firstElementChild,
        initialValue: this.modelValue || this.value,
        previewRender: this.previewRender,
        renderingConfig: {},
      }, this.configs);

      // modelValue와 initialValue 값 동기화
      if (configs.initialValue) {
        this.$emit('update:modelValue', configs.initialValue);
      }

      // 코드 하이라이트 활성화 여부 확인
      if (this.highlight) {
        configs.renderingConfig.codeSyntaxHighlighting = true;
      }

      // HTML 렌더링 설정
      marked.setOptions({ sanitize: this.sanitize });

      // 에디터 인스턴스 생성
      this.simplemde = new SimpleMDE(configs);

      // 사용자 정의 previewClass 추가
      const className = this.previewClass || '';
      this.addPreviewClass(className);

      // 이벤트 바인딩
      this.bindingEvents();

      // 초기화 완료
      this.$nextTick(() => {
        this.$emit('initialized', this.simplemde);
      });
    },
    bindingEvents() {
      this.simplemde.codemirror.on('change', (instance, changeObj) => {
        if (changeObj.origin === 'setValue') {
          return;
        }
        const val = this.simplemde.value();
        this.handleInput(val);
      });

      this.simplemde.codemirror.on('blur', () => {
        const val = this.simplemde.value();
        this.handleBlur(val);
      });
    },
    addPreviewClass(className) {
      const wrapper = this.simplemde.codemirror.getWrapperElement();
      const preview = document.createElement('div');
      wrapper.nextSibling.className += ` ${className}`;
      preview.className = `editor-preview ${className}`;
      wrapper.appendChild(preview);
    },
    handleInput(val) {
      this.isValueUpdateFromInner = true;
      this.$emit('update:modelValue', val);
      this.$emit('input', val);
    },
    handleBlur(val) {
      this.isValueUpdateFromInner = true;
      this.$emit('blur', val);
    },
  },
  destroyed() {
    this.simplemde = null;
  },
  watch: {
    modelValue(val) {
      // 내부 입력 처리 중이면 업데이트를 무시
      if (this.isValueUpdateFromInner) {
        this.isValueUpdateFromInner = false;
        return;
      }

      if (this.simplemde && this.simplemde.value() !== val) {
        const pos = this.simplemde.codemirror.getCursor(); // 커서 위치 저장
        this.simplemde.value(val); // SimpleMDE에 값 설정
        this.simplemde.codemirror.setCursor(pos); // 기존 커서 위치 복원
      }
    },
  },
};
</script>

<style>
.vue-simplemde .markdown-body {
  padding: 0.5em
}

.vue-simplemde .editor-preview-active, .vue-simplemde .editor-preview-active-side {
  display: block;
}
</style>
